import GhostContentAPI from "@tryghost/content-api";

const api = new GhostContentAPI({
  url: "https://ghost.victorkernes.com",
  key: "b4ee8d5856cca0a82749047208",
  version: "v3"
});

export async function getPosts() {
  return await api.posts
    .browse({
      limit: "all"
    })
    .catch(err => {
      console.error(err);
    });
}

function isFeatured(post) {
  if (process.env.NODE_ENV === "production") {
    return post.featured;
  }
  // load all posts locally
  return true;
}

export async function getFeaturedPosts() {
  return await getPosts().then(posts => posts.filter(isFeatured));
}

export async function getPostBySlug(slug) {
  return await api.posts
    .read({
      slug
    })
    .catch(err => {
      console.error("Error getting post by slug: ", slug);
    });
}
