import React from "react";
import * as contentful from "contentful";
import BlogItem from './blog/BlogItem';
import PageHeader from '../components/blog/PageHeader';
class Blog extends React.Component {
  state = {
    posts: []
  };
  client = contentful.createClient({
    space: "062rkadldcc7",
    accessToken:
      "997a2a4d28fcb9ac1260ec82ec0a115a06182890088d3a1c58a799d71008fc5f"
  });
  componentDidMount() {
    this.fetchPosts().then(this.setPosts);
  }
  fetchPosts = () => this.client.getEntries();
  setPosts = response => {
    this.setState({
      posts: response.items
    });
  };
  render() {
    return (
      <div>
        <PageHeader color="is-info" title="Code Blog">
          Your standard <strong>JavaScript</strong> programming blog,
          albeit, probably not very good, but I will at least try to keep it
          entertaining. This blog is a chronological mix of random posts on
          Angular, React, Functional Programming, and my{" "}
          <strong>project walkthroughs</strong>.
        </PageHeader>
        <br />
        {this.state.posts.map(({ fields }, i) => (
          <BlogItem key={i} {...fields} />
        ))}
      </div>
    );
  }
}
export default Blog;
