import * as React from "react";
import { getFeaturedPosts } from "~/api/ghost";
import Page, { SectionHeading } from "~/components/Page";
import { H1, LargeSubheading } from "~/components/Typography";
import OverthoughtSubscribeBox from "~/components/Overthought/Subscribe";
import OverthoughtList from "~/components/Overthought/List";

function Semantics(props) {
  const initialData = props.posts;
  const { data: posts } = useSWR("/api/getFeaturedPosts", getFeaturedPosts, {
    initialData
  });

  return (
    <Page withHeader>
      <SectionHeading data-cy="overthought">
        <H1>Overthought</H1>
        <LargeSubheading>
          Overthinking out loud about design, development, and building
          products.
        </LargeSubheading>

        <OverthoughtSubscribeBox />
      </SectionHeading>

      <OverthoughtList posts={posts} />
    </Page>
  );
}

Semantics.getInitialProps = async ({ res }) => {
  cacheSsrRes({ res });
  const posts = await getFeaturedPosts();
  return { posts: posts };
};

export default Semantics;
